sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Fetch data from the AI table
    var ga = new GlideAjax('DeviceDetailsFetcher');  // Call Script Include
    ga.addParam('sysparm_name', 'getDeviceDetails');
    ga.addParam('sysparm_device_name', newValue);  // Send device name
    ga.getXMLAnswer(function(response) {
        if (response) {
            var details = JSON.parse(response); // Parse JSON response

            // Populate fields
            g_form.setValue('u_string_model', details.u_model || '');
            g_form.setValue('u_price_9', details.u_new_price || '');
            g_form.setValue('u_device_description', details.u_device_description || '');
            g_form.setValue('u_warranty_experi_device_detail', details.u_warranty_expiration || '');
        } else {
            g_form.addErrorMessage('Device details not found.');
        }
    });
}
